import React ,{useState} from "react";
import Card from "../UI/Card";
import classes from './AddUser.module.css';
import Button from "../UI/Button";

const AddUser=(props)=>{
const [enteredUser,setEnteredValue]=useState('')
const [enteredAge,setEnteredAge]=useState('')

const addUserHandler=(e)=>{
    e.preventDefault()
    console.log(enteredValue , enteredAge)
}

const usernameHandler=(e)=>{setEnteredValue(e.target.value)}
const ageHandler=(e)=>{setEnteredAge(e.target.value)}


    return (
        <Card className={classes.input}>
        <form onSubmit={addUserHandler} >
        <label htmlFor="username">Username</label>
        <input id="username" type='text' onChange={usernameHandler}/>
        <label htmlFor="age" >Age (Years)</label>
        <input id="age" type='number' onChange={ageHandler}/>
        <Button type="submit">Add User</Button>
        </form>
        </Card>
    )
}
export default AddUser
